    val inputStroke by lazy {
        val parser = JsonParser()
        val obj = parser.parse(STROKE_TEXT) as JsonArray
        val res = KdTensor(1*MAX_STROKE_NUM*MAX_ONE_STROKE_LEN*INPUT_DIM)
        obj.forEachIndexed { strokeIndex, jsonElemOneStroke ->
            val stroke = jsonElemOneStroke as JsonArray
            val strokeOffset = strokeIndex*MAX_ONE_STROKE_LEN*INPUT_DIM
            stroke.forEachIndexed {posIndex, jsonElemOnePosArray ->
                val pos = jsonElemOnePosArray as JsonArray
                res.put(strokeOffset+posIndex*INPUT_DIM, pos[0].asInt)
                res.put(strokeOffset+posIndex*INPUT_DIM+1, pos[1].asInt)
                res.put(strokeOffset+posIndex*INPUT_DIM+2, pos[2].asInt)
            }
        }
        res
    }
    val inputDecoder by lazy {
        val  res = KdTensor(1*MAX_TOKEN_LEN)
        DECODER_INPUT.forEachIndexed { index, i ->  res.put(index, i) }
        res
    }

    val outputTensor = KdFTensor(1*MAX_TOKEN_LEN*VOCAB_SIZE)

    fun onButtonClick(v: View) {
        outputTensor.byteBuffer.rewind()
        interpreter.runForMultipleInputsOutputs(arrayOf(inputDecoder.toByteBuf(), inputStroke.toByteBuf()), mapOf(0 to outputTensor.byteBuffer))
        val outputArr = outputTensor.toFloatArray()
        Log.d("Tegashiki", outputArr.toString())
    }

